mapper.delete:
delete from usertag
where
tagvalue=#{tagvalue} and uid=#{uid}

mapper.select:

    select *
    from userInfo
    where id in (
		select uid
		from (
			select *
			from usertag
			where tagvalue in  (#{tag1},#{tag2},#{tag3},#{tag4},#{tag5})
		) as b
		group by uid
	);


    select *
    from userInfo
    where id in (
		select uid
		from (
			select * from usertag
			where tagvalue in (#{tag1},#{tag2},#{tag3},#{tag4},#{tag5})
		) as b
		group by uid
		having count(tagvalue) >= #{cnt}

	);

	select id, nickname, name, address2, git, lastDate, state, responsibility, isValid
	from userInfo
	where address2 like CONCAT('%',#{sido},'%')
    and address2 like CONCAT('%',#{gugun},'%')
    and address2 like CONCAT('%',#{dong},'%')
	and id in (
		select  uid
		from (
			select * from usertag
			where tagvalue in  (#{tag1},#{tag2},#{tag3},#{tag4},#{tag5})
		) as b
		group by uid
		having count(tagvalue) >= #{cnt}
	);
