mapper.select:
select *
from issue
where pid=#{pid} and issuestate=#{issuestate}

mapper.update:
update issue
set issuestate=#{issuestate},changeDay=now()
where issueid=#{issueid}

update issue
set response=#{response},changeDay=now()
where issueid=#{issueid}

update issue
set priority=#{priority},changeDay=now()
where issueid=#{issueid}
