mapper.select:
select * from
project_member where pid=#{pid} and
user_id=#{user_id}

mapper.insert:
insert into project_member
select #{pid}, #{user_id}, #{state}, #{priority}
from dual
where exists (select * from userInfo where id = #{user_id})
and exists (select * from project where pid = #{pid})
and not exists(select * from project_member where user_id =#{user_id} and pid = #{pid});

mapper.delete:
delete from project_member
where
pid=#{pid} and user_id=#{user_id}

mapper.update:
update project_member set
state=#{state}, priority=#{priority}
where pid=#{pid} and
user_id=#{user_id}
